{
    "title": "Content Data",
    "type": "object",
    "required": [
        "SectionID",
        "Title"
    ],
    "properties": {
        "SectionID": {
            "type": "string",
            "default": "MyAppScreens"
        },
        "Subtitle": {
            "type": "string",
            "default": "<span class='g-color-primary'>08.</span> App screens"
        },
        "Title": {
            "type": "string",
            "default": "Look <strong>how it works</strong>"
        },
        "Content": {
            "type": "string",
            "default": "Integer ut sollicitudin justo. Class aptent taciti sociosqu ad litora torquent per conubia nostra, per inceptos himenaeos."
        },
        "AllTitle": {
            "type": "string",
            "default": "Everything"
        },
        "Filters": {
            "type": "array",
            "format": "table",
            "title": "Filters",
            "items": {
                "type": "object",
                "title": "Filter",
                "properties": {
                    "Name": {
                        "type": "string",
                        "default": "category"
                    },
                    "Title": {
                        "type": "string",
                        "default": "Screen Category"
                    }
                }
            }
        },
        "Items": {
            "type": "array",
            "format": "table",
            "title": "Items",
            "items": {
                "type": "object",
                "title": "Item",
                "properties": {
                    "FilterName": {
                        "type": "string",
                        "default": "category"
                    },
                    "LargeImage": {
                        "type": "string",
                        "format": "pageImage",
                        "default": "/Unify263/one-pages/app/assets/img-temp/270x481/img1.jpg",
                        "options": {
                            "upload": {
                                "width": 270,
                                "height": 481,
                                "upload_handler": "uploadHandler",
                                "getPageConfig_handler": "getPageConfig"
                            }
                        },
                        "links": [
                            {
                                "href": "{{self}}"
                            }
                        ]
                    },
                    "Image": {
                        "type": "string",
                        "format": "pageImage",
                        "default": "/Unify263/one-pages/app/assets/img-temp/270x481/img1.jpg",
                        "options": {
                            "upload": {
                                "width": 270,
                                "height": 481,
                                "upload_handler": "uploadHandler",
                                "getPageConfig_handler": "getPageConfig"
                            }
                        },
                        "links": [
                            {
                                "href": "{{self}}"
                            }
                        ]
                    },
                    "ImageDesc": {
                        "type": "string",
                        "default": "Image description"
                    },
                    "Title": {
                        "type": "string",
                        "default": "Custom Title"
                    }
                }
            }
        }
    }
}

