    "title": "Content Data",
    "type": "object",
    "required": [
        "SectionID",
        "Title"
    ],
    "properties": {
        "SectionID": {
            "type": "string",
            "default": "MyHome"
        },
        "Title": {
            "type": "string",
            "default": "<strong>revolution in app</strong>"
        },
        "Subtitle": {
            "type": "string",
            "default": "<span class='g-color-primary'>06.</span> Why our app"
        },
        "Content": {
            "type": "string",
            "default": "Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Aenean commodo ligula eget dolor. Aenean massa. Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus."
        },
        "Image": {
            "type": "string",
            "format": "pageImage",
            "default": "/Unify263/one-pages/app/assets/img-temp/mockups/mockup1.png",
            "options": {
                "upload": {
                    "width": 450,
                    "height": 607,
                    "upload_handler": "uploadHandler",
                    "getPageConfig_handler": "getPageConfig"
                }
            },
            "links": [
                {
                    "href": "{{self}}"
                }
            ]
        },
        "ImageDesc": {
            "type": "string",
            "default": "Image description"
        },
        "Items": {
            "type": "array",
            "format": "table",
            "title": "Items",
            "items": {
                "type": "object",
                "title": "Item",
                "properties": {
                    "URL": {
                        "type": "string",
                        "default": "#"
                    },
                    "Title": {
                        "type": "string",
                        "default": "Download App"
                    },
                    "Color": {
                        "type": "string",
                        "default": "u-btn-darkpurple",
                        "enum": [
                            "u-btn-primary",
                            "u-btn-red",
                            "u-btn-lightred",
                            "u-btn-darkred",
                            "u-btn-blue",
                            "u-btn-indigo",
                            "u-btn-purple",
                            "u-btn-darkpurple",
                            "u-btn-pink",
                            "u-btn-orange",
                            "u-btn-deeporange",
                            "u-btn-aqua",
                            "u-btn-yellow",
                            "u-btn-cyan",
                            "u-btn-teal",
                            "u-btn-brown",
                            "u-btn-bluegray",
                            "u-btn-darkgray",
                            "u-btn-black"
                        ]
                    },
                    "Content": {
                        "type": "string",
                        "default": "<span class='d-block g-font-size-10 g-font-weight-400 g-opacity-0_6'>From Play Market</span> <i class='fa fa-android float-left g-font-size-26 g-mr-15'></i>"
                    }
                }
            }
        }
    }
