{
    "title": "Content Data",
    "type": "object",
    "required": [
        "SectionID",
        "Title"
    ],
    "properties": {
        "SectionID": {
            "type": "string",
            "default": "MyWhatsNew"
        },
        "Title": {
            "type": "string",
            "default": "We're always <strong>In trend</strong>"
        },
        "Subtitle": {
            "type": "string",
            "default": "<span class='g-color-primary'>04.</span> What's new"
        },
        "Items": {
            "type": "array",
            "format": "table",
            "title": "Items",
            "items": {
                "type": "object",
                "title": "Item",
                "properties": {
                    "Title": {
                        "type": "string",
                        "default": "Custom Title 1"
                    },
                    "Image": {
                        "type": "string",
                        "format": "pageImage",
                        "default": "/Unify263/one-pages/app/assets/img-temp/270x481/img1.jpg",
                        "options": {
                            "upload": {
                                "width": 270,
                                "height": 481,
                                "upload_handler": "uploadHandler",
                                "getPageConfig_handler": "getPageConfig"
                            }
                        },
                        "links": [
                            {
                                "href": "{{self}}"
                            }
                        ]
                    },
                    "ImageDesc": {
                        "type": "string",
                        "default": "Image description"
                    }
                }
            }
        }
    }
}

